INSERT INTO public.places (id, name, description, photoPath)
VALUES
    (gen_random_uuid(), 'Усьвинские столбы', E'Усьвинские столбы — это одно из самых известных природных чудес Пермского края. Эти уникальные скальные образования высотой до 120 метров расположены на берегу реки Усьва, в 200 километрах от Перми. Известняковые утесы образовались миллионы лет назад, и сегодня они являются не только природным памятником, но и важным объектом для экотуризма и исследования геологии. Величественные скалы привлекают путешественников, альпинистов и фотографов, предлагая захватывающие виды на реку и окрестные леса.\n\nСамая известная часть Усьвинских столбов — это так называемые «Столбы-купы», которые являются характерной особенностью этого географического района. Вокруг столбов образованы природные заповедники и экологические тропы, а также проводятся экскурсии для тех, кто хочет узнать больше о местной флоре и фауне. Этот район особенно красив в осенний и зимний периоды, когда окрестности утесов покрываются снегом, а река замерзает.\n\nУсьвинские столбы являются местом проведения экстремальных видов спорта — скалодрома и сплава по реке. Летом сюда часто приходят туристы, чтобы насладиться природой, а зимой — скатиться с ледяных склонов на санях и провести время в уютных домах. Внесены в список природных памятников региона и находятся под охраной государства.', ''),
    (gen_random_uuid(), 'Кунгурская ледяная пещера', E'Кунгурская ледяная пещера — одна из самых уникальных природных достопримечательностей России, расположенная в Пермском крае недалеко от города Кунгур. Это огромный карстовый комплекс, известный своими ледяными образованиями, которые остаются неповрежденными даже в самые жаркие месяцы года. Общая длина пещеры составляет более 5 километров, из которых около 1,5 километров доступны для туристов. Пещера была открыта еще в XVIII веке и с тех пор является важным объектом для научных исследований и туризма.\n\nПещера состоит из нескольких залов, каждый из которых имеет свою уникальную атмосферу. Внутри можно увидеть ледяные сталактиты и сталагмиты, прозрачные ледяные озера и хрустальные образования, которые невозможно встретить нигде в мире. Лед в пещере никогда не тает, благодаря особым микроклиматическим условиям. Этот уникальный природный феномен стал причиной того, что Кунгурская ледяная пещера была включена в список природных памятников.\n\nКроме того, пещера является объектом научных исследований по изучению карстовых образований и подземных экосистем. Вокруг пещеры создана инфраструктура для туристов, включая специальные маршруты и экскурсии с опытными гидами, которые помогают лучше понять геологическое и природное наследие этого уникального места. В зимнее время здесь открываются ледяные скульптуры, а в летнее — проходят фестивали, посвященные пещерам и их истории.', '')
ON CONFLICT (id) DO UPDATE
SET
    name = EXCLUDED.name,
    description = EXCLUDED.description,
    photoPath = EXCLUDED.photoPath;
